mapper.select:
SELECT MEMBER_PW
FROM MEMBER
WHERE MEMBER_ID = #{id}

SELECT MEMBER_ID 
FROM MEMBER
WHERE MEMBER_ID = #{inputId}

mapper.update:
	UPDATE MEMBER
	SET member_pw = #{member_pw}, member_nickname = #{member_nickname},
	member_phonenumber = #{member_phonenumber}, member_email = #{member_email},
	member_birth = #{member_birth}, member_introduce = #{member_introduce}
	WHERE MEMBER_ID = #{member_id}
